                    this.appendChild(dot);

                    // Remove the dot once animation completes
                    setTimeout(() => {
                        if (dot.parentNode) {
                            dot.parentNode.removeChild(dot);
                        }
                    }, 1500);
                }

                // Reset animation flag after animation completes
                setTimeout(() => {
                    isAnimating = false;
                }, 800);
            });
        }
    });



